import React, { Component } from 'react';
import styles from './Scraper.module.css';
import StickyHeadTable from '../../components/StickyHeadTable/StickyHeadTable';
import Header from '../../components/Header/Header';
import firebase from '../../Firebase';
class Scraper extends Component {

    state = {
        sourceLinks: false
    }

    componentDidMount(){
        /*axios.get('http://localhost:5000')
          .then((resp) => {
            this.setState({items: resp.data});
          })
          .catch((err) => {
            console.log(err);
          });*/

          const db = firebase.firestore();
          db.collection('sources_list').get().then((snapshot) => {
           // this.setState({items: snapshot});
           let items = []; 
           snapshot.forEach(doc => {
              //console.log(doc.id, '=>', doc.data());
              items.push(doc.data());
            });
            this.setState({sourcesList: items});
          }).catch((error) => {
            console.error("Error adding document: ", error);
          });

          //this.saveItemsHandler();

      }

      sourceLinkPageHandler = ()=>{
        let newState = {...this.state};
        newState.sourceLinks = true;
        this.setState(newState);
      }


    render (){

        return (
            <div>
            <Header/>
            <StickyHeadTable/>
            </div>
        )

    }

}

export default Scraper;
