import React,{Component} from 'react'
import styles from './Order.module.css';
import Price from '../Price/Price'

class Order extends Component {

  changeDays = (event) => {
      this.props.changeDays(event);
  }

  changePayment = (event) => {
      this.props.changePayment(event);
  }




	render(){

    let newPrice = "";
    if(this.props.payment.type === 2){
      newPrice =  <NewPrice
                      points={this.props.points}
                  />
    }


    return (
  		<table id={styles.customers}>
        <tbody>
          <tr>
             <td>Movie Name</td>
            <td>{this.props.item.movie_name}</td>
          </tr>
          <tr>
             <td>Movie Type</td>
            <td>{this.props.item.mtype_name}</td>
          </tr>
          <tr>
             <td>Days</td>
            <td>
              <select id="days" onChange={this.changeDays}>
                  <option default value="1">1</option>
                  <option value="2">2</option>
                  <option value="3">3</option>
                  <option value="4">4</option>
                  <option value="5">5</option>
                  <option value="6">6</option>
                  <option value="7">7</option>
                  <option value="8">8</option>

                </select>
            </td>
          </tr>
           <tr>
             <td>Price</td>
             <Price 
                  price={this.props.item.price}
                  days={this.props.days}
                  type={this.props.item.type}
                  total_price = {this.props.total_price}
              />
           </tr>
           {newPrice}
          <tr>
             <td>Payment Type</td>
            <td>
                <select id="payment_type" onChange={this.changePayment}>
                  <option default value="1">Cash</option>
                  <option value="2">Points</option>
                </select>
            </td>
          </tr>
        </tbody>
      </table>
  	)
  }

}

export default Order
