import React, { Component } from 'react';
import styles from './Scraper.module.css';
import StickyHeadTable from '../../components/StickyHeadTable/StickyHeadTable';
import Sources from '../../components/Sources/Sources';
import SourceLinks from '../../components/SourceLinks/SourceLinks';
class Scraper extends Component {

    state = {
        sourceLinks: false
    }

    componentDidMount(){
        /*axios.get('http://localhost:5000')
          .then((resp) => {
            this.setState({items: resp.data});
          })
          .catch((err) => {
            console.log(err);
          });*/

          const db = firebase.firestore();
          db.collection('sources_list').get().then((snapshot) => {
           // this.setState({items: snapshot});
           let items = []; 
           snapshot.forEach(doc => {
              //console.log(doc.id, '=>', doc.data());
              items.push(doc.data());
            });
            this.setState({items: items});
          }).catch((error) => {
            console.error("Error adding document: ", error);
          });

          //this.saveItemsHandler();

      }

      sourceLinkPageHandler = ()=>{
        this.setState({ sourceLinks: true });
      }


    render (){

        let searchClasses = [];
        searchClasses.push("fa");
        searchClasses.push("fa-search");

        let sourceLinks = "";
        if(this.state.SourceLinks){
            return <SourceLinks/>;
        }

        return (
            <div>
            <div className={styles.topnav}>
            <a className={styles.active} href="#home">Home</a>
            <a href="#about">About</a>
            <a href="#contact">Contact</a>
            <div className={styles.searchContainer}>
                <input type="text" placeholder="Search.." name="search" />
                <button type="submit"><i className={searchClasses.join(" ")}></i></button>
            </div>
            </div>
            {sourceLinks}
            <Sources 
                clicked={this.sourceLinkPageHandler}
                itemsList={this.state.itemsList}
            />
            </div>



        )

    }

}

export default Scraper;
