import React,{ Component } from 'react';
import axios from 'axios';
import styles from './SourceLinks.module.css';
import firebase from '../../Firebase';

class SourceLinks extends Component {

  componentDidMount(){
    /*axios.get('http://localhost:5000')
      .then((resp) => {
        this.setState({items: resp.data});
      })
      .catch((err) => {
        console.log(err);
      });*/

      const db = firebase.firestore();
      db.collection('sources').where("siteUrl", "==", this.props.ctUrl).get().then((snapshot) => {
       // this.setState({items: snapshot});
       let items = []; 
       snapshot.forEach(doc => {
          //console.log(doc.id, '=>', doc.data());
          items.push(doc.data());
        });
        this.setState({items: items});
      }).catch((error) => {
        console.error("Error adding document: ", error);
      });

      //this.saveItemsHandler();

  }





  render (){



    let itemsList = <tr></tr>;
    if(this.state.items){
      console.log(this.state)
      itemsList = Object.keys(this.state.items)
      .map(key => {
        let item = this.state.items[key];
      return <tr key={key}>
              <td><a target="_blank" href={item.url}>{item.caption}</a></td>
              <td>{item.isRelative.toString()}</td>
              <td>{item.lastModified}</td>
              <td>{item.contentEncoding}</td>
              <td>{item.contentSize}</td>
              <td>{item.server}</td>
             </tr>;
      });
    }


    return (
      <div>
        <table id={styles.customers}>
        <thead>
            <tr>
                <th>Sources</th>
            </tr>
            <tr>
                <th>Link</th>
                <th>Relative</th>
                <th>Last Modified</th>
                <th>Content Encoding</th>
                <th>Content Size</th>
                <th>Server</th>
            </tr>
        </thead>
        <tbody>
          {itemsList}

        </tbody>
      </table>
      </div>
    );
  }
}

export default SourceLinks;
