import React,{ Component } from 'react';
import axios from 'axios';
import styles from './StickyHeadTable.module.css';
import firebase from '../../Firebase';

class StickyHeadTable extends Component {

  state = {
  }

  saveItemsHandler = (data) =>{

    console.log(data)
    let encString = window.btoa(data.siteUrl+data.caption+data.url);
    const db = firebase.firestore();
    db.collection('sources').doc(encString).set(data).then((docRef) => {
      console.log(docRef)
    }).catch((error) => {
      console.error("Error adding document: ", error);
    });

  }

  componentDidMount(){
    axios.get('http://localhost:5000')
      .then((resp) => {
        this.setState({items: resp.data});
      })
      .catch((err) => {
        console.log(err);
      });

      //this.saveItemsHandler();

  }





  render (){



    let itemsList = <tr></tr>;
    if(this.state.items){
      console.log(this.state)
      itemsList = Object.keys(this.state.items)
      .map(key => {
        let item = this.state.items[key];
      return <tr key={key}>
              <td><a target="_blank" href={item.url}>{item.caption}</a></td>
              <td>{item.isRelative.toString()}</td>
              <td>{item.lastModified}</td>
              <td>{item.contentEncoding}</td>
              <td>{item.contentSize}</td>
              <td>{item.server}</td>
              <td ><button onClick={() => this.saveItemsHandler(item)}>Save</button></td>
             </tr>;
      });
    }


    return (
      <div>
        <table id={styles.customers}>
        <thead>
            <tr>
                <th>Link</th>
                <th>Relative</th>
                <th>Last Modified</th>
                <th>Content Encoding</th>
                <th>Content Size</th>
                <th>Server</th>
                <th>Saved</th>
            </tr>
        </thead>
        <tbody>
          {itemsList}

        </tbody>
      </table>
      </div>
    );
  }
}

export default StickyHeadTable;
