import React,{ Component } from 'react';
import axios from 'axios';
import styles from './Rent.module.css';
import Order from '../Order/Order'
import OrderReceipt from '../OrderReceipt/OrderReceipt'
//import firebase from '../../Firebase';

class Rent extends Component {

  state = {
      days: 1,
      paymentType: 1,
      itemRented: false
  }

  componentDidMount(){

      console.log(this.props.item);

      this.setState(
        {
         id: this.props.id,
         type: this.props.type, 
        }
      );
  }

  rentHandler = () => {



      const params = {
        user_id: "a1234",
        movie_id: 4,
        payment_type: 1
      }

      axios.post('http://34.230.13.18:8081/v1/orders',params)
          .then((resp) => {
            this.setState(
                {
                  itemRented: true,
                  orderId: resp.orderId
                }
              );
          })
          .catch((err) => {
            console.log(err);
          });


  }

  changeDays = (event) => {

    console.log(event)

    let total_price = this.props.item.price;
    let days = event.target.value;

    if(this.props.item.type === 1){

      total_price =  this.props.item.price*event.target.value;

    }else if(this.props.item.type === 2 && days <= 3){

      total_price = this.props.item.price;

    }else if(this.props.item.type === 2 && days > 3){

      days = days-3+1;
      total_price = this.props.item.price*days;

    }else if(this.props.item.type === 3 && days <= 5){

      total_price = this.props.item.price;

    }else if(this.props.item.type === 3 && days > 5){

      days = days-5+1;
      total_price = this.props.item.price*event.target.value;

    }

    this.setState(
      {
        days: parseInt(days),
        total_price: total_price
      }
      );

  }

  changePayment = (event) => {
    this.setState({paymentType: parseInt(event.target.value)});
  }



  render (){

    let modalContent = <Order
                            changeDays={this.changeDays.bind(this)}
                            changePayment={this.changePayment}
                            item={this.props.item}
                            days={this.state.days}
                            total_price={this.props.item.price}
                        />

    if(this.state.orderId){

           modalContent = <OrderReceipt
                            days={this.state.days}
                            item={this.props.item}
                            orderId={this.state.orderId}
                            price={this.state.total_price}
                            paymentType={this.state.paymentType}

                      />

    }else{

        if(this.state.total_price){

            modalContent = <Order
                                changeDays={this.changeDays.bind(this)}
                                changePayment={this.changePayment}
                                item={this.props.item}
                                days={this.state.days}
                                total_price={this.state.total_price}
                            />
        }

    }



    return (

      <div>

        {modalContent}

        <div>
            <button onClick={this.rentHandler}>Rent</button>
        </div>
      </div>
    );
  }
}

export default Rent;
