import React,{ Component } from 'react';
import axios from 'axios';
import styles from './MoviesAdmin.module.css';
//import firebase from '../../Firebase';

class MoviesAdmin extends Component {


  deleteClicked = (item,key) => {
      console.log("item1234",item)
      this.props.deleteClicked(item,key);
  }

  editClicked = (item,key) => {
      console.log("item1234",item)
      this.props.editClicked(item,key);
  }

  render (){

    let items = <tr></tr>;
    if(this.props.itemsList){

      items = Object.keys(this.props.itemsList)
      .map(key => {

        console.log(key)

        let item = this.props.itemsList[key];

        return <tr key={key}>
                <td>{item.movie_name}</td>
                <td>{item.mtype_name}</td>
                <td>{item.rented}</td>
                <td>
                  <button onClick={()=>this.props.editClicked(item.id,key)}>Delete</button>
                  <button onClick={()=>this.props.deleteClicked(item,key)}>Edit</button>
                </td>
               </tr>;
        });
    }

    return (

      <div>

         <button >Add Movie</button>
        <table id={styles.customers}>
        <thead>
            <tr>
                <th>Name</th>
                <th>Type</th>
                <th>Availability</th>
                <th>Action</th>
            </tr>
        </thead>
        <tbody>
          {items}
        </tbody>
      </table>
      </div>

    );
  }
}

export default MoviesAdmin;
