import React, { Component } from 'react';
import styles from './MovieRental.module.css';
import axios from 'axios';
import Header from '../../components/Header/Header';
import Movies from '../../components/Movies/Movies';
import Rent from '../../components/Rent/Rent';
import Modal from '../../components/UI/Modal/Modal';
import firebase from '../../Firebase';

class MovieRental extends Component {

    state = {

        openModal: false,
        currentItem: {},
        loggedIn: false
    }

    addUserHandler = (id,name,email_id) => {

        var data = new FormData();
        data.append('id', id);
        data.append('name', name);
        data.append('email_id', email_id);

        axios.post('http://34.230.13.18:8081/v1/users',data)
          .then((resp) => {
            if(resp.data.inserted === 1){

                let newState = {...this.state};
                newState.userData = {
                  id: id,
                  name: name,
                  email_id: email_id,
                  points: 0
                }
                this.setState(newState);

            }else{


                console.log("=========");
                console.log(resp.data);
                console.log("=========");

                let newState = {...this.state};
                newState.userData = {
                  id: resp.data[0].id,
                  name: resp.data[0].name,
                  email_id: resp.data[0].email_id,
                  points: resp.data[0].points
                }
                this.setState(newState);


            }

          })
          .catch((err) => {
            console.log(err);

          });

    }


    loginHandler = () =>{
      const state = this;

      let provider = new firebase.auth.GoogleAuthProvider();

      firebase.auth().signInWithPopup(provider).then(function(result) {

        const token = result.credential.accessToken;
        const user = result.user;
        const id = user.uid;
        const name = user.displayName;
        const email_id = user.email;

        state.addUserHandler(id,name,email_id);

        state.setState({loggedIn:true});
        ;
        // ...
      }).catch(function(error) {
          console.log(error);
      });
    }

    logoutHandler = () => {

        const state = this;

        firebase.auth().signOut().then(function() {

          console.log("Logged Out");
          state.setState({loggedIn:false});
          // Sign-out successful.
        }, function(error) {
          console.log(error);
          // An error happened.
        });


    }

    authStateChanged = () => {
        const state = this;

          firebase.auth().onAuthStateChanged(function(user) {
            if(user){
              state.setState({loggedIn:true});
              state.getUserHandler();

            }else{
              state.setState({loggedIn:false});
              console.log("Not logged in")

            }

        });

    }


    componentDidMount(){

        this.getMoviesHandler();
        this.logoutHandler();
        //this.getUserHandler();
        //this.authStateChanged();

      }

      getUserHandler = (id) =>{

          axios.get('http://34.230.13.18:8081/v1/user/'+id)
          .then((resp) => {

              console.log(resp.data[0]);

              this.setState({userData: resp.data[0]});

          })
          .catch((err) => {
            console.log(err);
          });


      }


      getMoviesHandler = () => {

          axios.get('http://34.230.13.18:8081/v1/movies')
          .then((resp) => {
            this.setState({items: resp.data});
          })
          .catch((err) => {
            console.log(err);
          });

      }


      modalClosed = () => {

        console.log("modalClosed");
        let newState = {...this.state};
        newState.openModal = false;
        this.setState(newState);

        this.getUserHandler();

      }

      rentOutHandler = (item)=>{
        let newState = {...this.state};
        newState.openModal = true;
        newState.currentItem = item;
        console.log(newState);
        this.setState(newState);
      }




    render (){

        let searchClasses = [];
        searchClasses.push("fa");
        searchClasses.push("fa-search");

        let moviesList = "";

        moviesList = <Movies 
                      clicked={this.rentOutHandler.bind(this)}
                      items={this.state.items}

                    />;
        let rentList = "";

        if(this.state.openModal){
          rentList = <Rent 
                        item={this.state.currentItem}
                        closeModalClicked={this.modalClosed}
                        userData={this.state.userData}
                      />;   

        }

        return (
            <div>
            <Header 
                clicked={this.loginHandler}/>
            <Modal
                show={this.state.openModal}
                modalClosed={this.modalClosed}
            >
              {rentList}
            </Modal>
              {moviesList}
            </div>



        )

    }

}

export default MovieRental;
