import React, { Component } from 'react';
import styles from './MovieRental.module.css';
import axios from 'axios';
import Header from '../../components/Header/Header';
import Movies from '../../components/Movies/Movies';
import Rent from '../../components/Rent/Rent';
import Modal from '../../components/UI/Modal/Modal';
import firebase from '../../Firebase';

class MovieRental extends Component {

    state = {

        openModal: false,
        currentItem: {},
        loggedIn: false
    }


    loginHandler = () =>{

      let provider = new firebase.auth.GoogleAuthProvider();

      firebase.auth().signInWithPopup(provider).then(function(result) {

        let token = result.credential.accessToken;
        let user = result.user;
        this.setState({loggedIn:true});
        console.log(user);

        //showUserName(user.displayName);


        // ...
      }).catch(function(error) {
          console.log(errorMessage);
      });
    }

    logoutHandler = () => {

        firebase.auth().signOut().then(function() {

          console.log("Logout");
          this.setState({loggedIn:false});
          // Sign-out successful.
        }, function(error) {
          // An error happened.
        });


    }


    componentDidMount(){

        this.getMoviesHandler();
        this.getUserHandler();

      }

      getUserHandler = () =>{

          axios.get('http://34.230.13.18:8081/v1/user/a1234')
          .then((resp) => {

              console.log(resp.data[0]);

              this.setState({userData: resp.data[0]});

          })
          .catch((err) => {
            console.log(err);
          });


      }


      getMoviesHandler = () => {

          axios.get('http://34.230.13.18:8081/v1/movies')
          .then((resp) => {
            this.setState({items: resp.data});
          })
          .catch((err) => {
            console.log(err);
          });

      }


      modalClosed = () => {

        console.log("modalClosed");
        let newState = {...this.state};
        newState.openModal = false;
        this.setState(newState);

        this.getUserHandler();

      }

      rentOutHandler = (item)=>{
        let newState = {...this.state};
        newState.openModal = true;
        newState.currentItem = item;
        console.log(newState);
        this.setState(newState);
      }




    render (){

        let searchClasses = [];
        searchClasses.push("fa");
        searchClasses.push("fa-search");

        let moviesList = "";


        /*if(this.state.sourceLinks){
            console.log("Rendering new component")
            sourceLinks = <SourceLinks 
                            ctUrl={this.state.ctUrl}
                          />;
        }else{*/
            moviesList = <Movies 
                          clicked={this.rentOutHandler.bind(this)}
                          items={this.state.items}

                        />;
       // }

            let rentList = "";

            if(this.state.openModal){
              rentList = <Rent 
                            item={this.state.currentItem}
                            closeModalClicked={this.modalClosed}
                            userData={this.state.userData}
                          />;   

            }

        return (
            <div>
            <Header 
                clicked={this.loginHandler}/>
            <Modal
                show={this.state.openModal}
                modalClosed={this.modalClosed}
            >
              {rentList}
            </Modal>
              {moviesList}
            </div>



        )

    }

}

export default MovieRental;
