import React,{ Component } from 'react';
import axios from 'axios';
import styles from './MoviesAdmin.module.css';
//import firebase from '../../Firebase';

class MoviesAdmin extends Component {


  deleteClicked = (item,key) => {
      console.log("item1234",item)
      this.props.deleteClicked(item,key);
  }

  editClicked = (item,key) => {
      console.log("item1234",item)
      this.props.editClicked(item,key);
  }

  render (){

    let items = <tr></tr>;
    if(this.props.itemsList){

      items = Object.keys(this.props.itemsList)
      .map(key => {

        console.log(key)

        let item = this.props.itemsList[key];

        let availability = "Available";

        if(item.rented === 1){
          availability = "Not Available";
        }

        return <tr key={key}>
                <td>{item.movie_name}</td>
                <td>{item.mtype_name}</td>
                <td>{availability}</td>
                <td>
                  <button onClick={()=>this.props.deleteClicked(item.id,key)}>Delete</button>

                  <button onClick={()=>this.props.editClicked(item,key)}>Edit</button>
                </td>
               </tr>;
        });
    }

    return (

      <div>

        <table id={styles.customers}>
        <thead>
            <tr>
                <th>Name</th>
                <th>Type</th>
                <th>Availability</th>
                <th>Action</th>
            </tr>
        </thead>
        <tbody>
          {items}
        </tbody>
      </table>
      </div>

    );
  }
}

export default MoviesAdmin;
