      })
      .catch((err) => {
        console.log(err);
      });

  }*/

  clicked = (item) => {
    this.props.clicked(item);
  }



  render (){

    let itemsList = <tr></tr>;
    if(this.state.items){
      console.log(this.state)
      itemsList = Object.keys(this.state.items)
      .map(key => {
        let item = this.state.items[key];
      return <tr key={key}>
              <td>{item.movie_name}</td>
              <td>{item.mtype_name}</td>
              <td><button onClick={()=>this.clicked(item)}>Rent</button></td>
             </tr>;
      });
    }


    return (

      <div>

        <table id={styles.customers}>
        <thead>
            <tr>
                <th>Name</th>
                <th>Type</th>
                <th>Action</th>
            </tr>
        </thead>
        <tbody>
          {itemsList}

        </tbody>
      </table>
      </div>
    );
  }
}

export default Movies;
