import React,{ Component } from 'react';
import axios from 'axios';
import styles from './Rent.module.css';
import Order from '../Order/Order'
import OrderReceipt from '../OrderReceipt/OrderReceipt'
import Button from '../UI/Button/Button'
//import firebase from '../../Firebase';

class Rent extends Component {

  state = {
      days: 1,
      paymentType: 1,
      itemRented: false
  }

  componentDidMount(){

      this.setState(
        {
         id: this.props.item.id,
         type: this.props.item.type, 
         total_price: this.props.item.price,
        }
      );
  }

  rentHandler = () => {

      var data = new FormData();
      data.append('user_id', "a1234");
      data.append('movie_id', 4);
      data.append('payment_type', 1);
      data.append('points', 1);

      axios.post('http://34.230.13.18:8081/v1/orders',data)
          .then((resp) => {
            console.log(resp);
            const newState = {
                              ...this.state,
                              itemRented: true,
                              orderId: resp.data.orderId
                           };
            console.log(newState);
            this.setState(newState);

          })
          .catch((err) => {
            console.log(err);

          });


  }

  changeDays = (event) => {

    let total_price = this.props.item.price;
    let days = event.target.value;

    if(this.props.item.type === 1){

      total_price =  this.props.item.price*event.target.value;

    }else if(this.props.item.type === 2 && days <= 3){

      total_price = this.props.item.price;

    }else if(this.props.item.type === 2 && days > 3){

      days = days-3+1;
      total_price = this.props.item.price*days;

    }else if(this.props.item.type === 3 && days <= 5){

      total_price = this.props.item.price;

    }else if(this.props.item.type === 3 && days > 5){

      days = days-5+1;
      total_price = this.props.item.price*event.target.value;

    }

    this.setState(
      {
        days: parseInt(days),
        total_price: total_price
      }
      );

  }

  changePayment = (event) => {
    this.setState({paymentType: parseInt(event.target.value)});
  }


  render (){

    let modalContent = <Order
                            changeDays={this.changeDays.bind(this)}
                            changePayment={this.changePayment}
                            item={this.props.item}
                            days={this.state.days}
                            total_price={this.props.item.price}
                        />

    let button =  <Button clicked={this.rentHandler} btnType="Success">ORDER</Button>

    if(this.state.orderId){

          console.log(this.state)

           modalContent = <OrderReceipt
                            days={this.state.days}
                            item={this.props.item}
                            orderId={this.state.orderId}
                            price={this.state.total_price}
                            paymentType={this.state.paymentType}

                      />


            button = <Button 
                        clicked={this.props.closeModalClicked} 
                        btnType="Success">DONE</Button>

    }else{

        if(this.state.total_price){

            modalContent = <Order
                                changeDays={this.changeDays.bind(this)}
                                changePayment={this.changePayment}
                                item={this.props.item}
                                days={this.state.days}
                                total_price={this.state.total_price}
                            />
        }

    }



    return (

      <div>

        {modalContent}

        {button}


      </div>
    );
  }
}

export default Rent;
