    state = {

    }

    componentDidMount(){

        axios.get('http://34.230.13.18:8080/v1/movies')
          .then((resp) => {
            this.setState({items: resp.data});
          })
          .catch((err) => {
            console.log(err);
          });



      }

      sourceLinkPageHandler = (url)=>{
        let newState = {...this.state};
        newState.sourceLinks = true;
        newState.ctUrl = url;
        this.setState(newState);
      }


    render (){

        let searchClasses = [];
