import React, { Component } from 'react';
import styles from './Sources.module.css';
import StickyHeadTable from '../../components/StickyHeadTable/StickyHeadTable';
import SourceList from '../../components/Sources/Sources';
import SourceLinks from '../../components/SourceLinks/SourceLinks';
import firebase from '../../Firebase';
class Sources extends Component {

    state = {
        sourceLinks: false,
        siteUrl: ""
    }

    componentDidMount(){
        /*axios.get('http://localhost:5000')
          .then((resp) => {
            this.setState({items: resp.data});
          })
          .catch((err) => {
            console.log(err);
          });*/

          const db = firebase.firestore();
          db.collection('sources_list').get().then((snapshot) => {
           // this.setState({items: snapshot});
           let items = []; 
           snapshot.forEach(doc => {
              //console.log(doc.id, '=>', doc.data());
              items.push(doc.data());
            });
            this.setState({sourcesList: items});
          }).catch((error) => {
            console.error("Error adding document: ", error);
          });

          //this.saveItemsHandler();

      }

      sourceLinkPageHandler = (url)=>{
        let newState = {...this.state};
        newState.sourceLinks = true;
        newState.ctUrl = url;
        this.setState(newState);
      }


    render (){

        let searchClasses = [];
        searchClasses.push("fa");
        searchClasses.push("fa-search");

        let sourceLinks = "";

        if(this.state.sourceLinks){
            console.log("Rendering new component")
            sourceLinks = <SourceLinks 
                            ctUrl={this.state.ctUrl}
                          />;
        }else{
            sourceLinks = <SourceList 
            clicked={this.sourceLinkPageHandler.bind(this)}
            itemsList={this.state.sourcesList}
            />;
        }

        return (
            <div>
            <div className={styles.topnav}>
            <a href="/">Home</a>
            <a className={styles.active} href="/sources">Sources</a>
            <div className={styles.searchContainer}>
                <input type="text" placeholder="Search.." name="search" />
                <button type="submit"><i className={searchClasses.join(" ")}></i></button>
            </div>
            </div>
            {sourceLinks}
            </div>



        )

    }

}

export default Sources;
